<?phprequire_once 'lib/Twig/Autoloader.php';Twig_Autoloader::register();$loader = new Twig_Loader_Filesystem('templates');$twig = new Twig_Environment($loader, array(  'cache' => 'templates/compilation_cache',));$twig->setCache(false);require_once("DB.php");$db = new DB();$naire_id = isset($_GET['id']) ? $_GET['id'] : 1;$questionnaire = $db->getQuestionnaire($naire_id);$questions = $db->getQuestions($naire_id);echo $twig->render("questionnaire.html", array("questionnaire" => $questionnaire, "questions" => $questions));?>